
CREATE DATABASE blog_db;

CREATE TABLE blog_db.users (
    user_id INT(10) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) NOT NULL,
    password VARCHAR(150) NOT NULL,
    reg_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

CREATE TABLE blog_db.articles (
    article_id INT(10) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(50) NOT NULL,
    body TEXT(255) NOT NULL,
    image VARCHAR(250),
    created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    user_id INT(10),
    cat_id VARCHAR(50) NOT NULL,
    comment_id INT(10),
    views INT(250) NOT NULL DEFAULT 0
);

CREATE TABLE blog_db.categories (
    category_id INT(10) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    cat_name VARCHAR(50) NOT NULL
);

CREATE TABLE blog_db.comments (
    comment_id INT(10) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    body TEXT(250) NOT NULL,
    author_name VARCHAR(150),
    author_email VARCHAR(150),
    created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    article_id INT(10) NOT NULL
);

CREATE TABLE blog.slider(
    id INT(10) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    image_name VARCHAR(150) NOT NULL DEFAULT "placeholoder.jpg",
    slide_heaeding VARCHAR(250)
);





